import React, { useState } from "react";

function PlacesToGo() {
  //restructered code so we have a list of todos, list out each one as a p tag, using array in state
  const [todos, setTodos] = useState([]);
  //preparing state to keep track of user input in user field
  const [input, setInput] = useState("");
  const addTodo = (e) => {
    e.preventDefault();
    setTodos([...todos, input]);
    setInput("");
  };

  //reset button
  const handleReset = () => {
    Array.from(document.querySelectorAll("input")).forEach(
      (input) => (input.value = "")
    );
    this.setState({
      itemvalues: [{}],
    });
  };

  //show voter and calc upon click
  _showMessage = (bool) => {
    this.setState({
      showMessage: bool,
    });
  };

  return (
    <div>
      <h2>Which place shall we go?</h2>

      <div>
        Click the buttons to toggle the message <br />
        <button onClick={this._showMessage.bind(null, true)}>show</button>
        <button onClick={this._showMessage.bind(null, false)}>hide</button>
        {this.state.showMessage && <div>hello world!</div>}
      </div>

      <form>
        <input
          value={input}
          onChange={(e) => setInput(e.target.value)}
          type="text"
        ></input>
        <button type="submit" onClick={addTodo}>
          Add Place to GO
        </button>
        <button onClick={handleReset}>RESET</button>
      </form>

      <h3>List of To-DOSSSSS</h3>
      {todos.map((todo) => (
        <p>{todo}</p>
      ))}
    </div>
  );
}

export default PlacesToGo;
