import React, { useState } from "react";

function MealVoter(props) {
  //object?
  const [count, setCount] = useState(0);

  //functions
  const increment = () => {
    useState(count + 1);
  };

  const decrement = () => {
    useState(count - 1);
  };

  return (
    //decrement function

    <div>
      <p>Hello {props.name}, welcome to the MealVoter!</p>
      <p>Your total is: {count} </p>
      <p>Vote Up</p>
      <button onClick={increment}>+</button>
      <p>Vote Down</p>
      <button onClick={decrement}>-</button>
    </div>
  );
}

export default MealVoter;
