import React, { useState } from "react";

function PlacesToGoTwo() {
  //restructered code so we have a list of todos, list out each one as a p tag, using array in state
  const [todos, setTodos] = useState([]);
  //preparing state to keep track of user input in user field
  const [inputtwo, setInputTwo] = useState("");
  const addTodo = (e) => {
    e.preventDefault();
    setTodos([...todos, inputtwo]);
    setInputTwo("");
  };

  //reset button
  const handleReset = () => {
    Array.from(document.querySelectorAll("inputtwo")).forEach(
      (inputtwo) => (inputtwo.value = "")
    );
    this.setState({
      itemvalues: [{}],
    });
  };

  return (
    <div>
      <h2>Which place shall we go?</h2>

      <form>
        <input
          value={inputtwo}
          onChange={(e) => setInputTwo(e.target.value)}
          type="text"
        ></input>
        <button type="submit" onClick={addTodo}>
          Add Place to GO
        </button>
        <button onClick={handleReset}>RESET</button>
      </form>

      <h3>List of To-DOSSSSS</h3>
      {todos.map((todo) => (
        <p>{todo}</p>
      ))}
    </div>
  );
}

export default PlacesToGoTwo;
