    totalTip: "",
    tipPP: "",
  };
  render() {
    return (
      <div>
        <h1>Calculator</h1>
        <form>
          <input>Enter your total amount:</input>
          <button>Total $</button>
          <input>How was your service?</input>
        </form>
      </div>
    );
  }
}

export default TipCalc;
