import React, { Component } from "react";
import { render } from "react-dom";
import DemoOne from "./DemoOne";
import TipCalc from "./TipCalc";

class App extends Component {
  constructor() {
    super();
    this.state = {
      name: "React",
      showHideDemoOne: false,
      showHideTipCalc: false,
    };
    this.hideComponent = this.hideComponent.bind(this);
  }

  hideComponent(name) {
    console.log(name);
    switch (name) {
      case "showHideDemoOne":
        this.setState({ showHideDemoOne: !this.state.showHideDemoOne });
        break;
      case "showHideTipCalc":
        this.setState({ showHideTipCalc: !this.state.showHideTipCalc });
        break;
      default:
      //null;
    }
  }

  render() {
    const { showHideDemoOne, showHideTipCalc } = this.state;
    return (
      <div>
        {showHideDemoOne && <DemoOne />}
        <hr />
        {showHideTipCalc && <MealVoter />}
        <hr />
        <div>
          <button onClick={() => this.hideComponent("showHideDemoOne")}>
            Click to hide Demo1 component
          </button>
          <button onClick={() => this.hideComponent("showHideTipCalc")}>
            Click to hide Demo2 component
          </button>
        </div>
      </div>
    );
  }
}

render(<App />, document.getElementById("root"));

