import React from "react";

class TipCalc extends React.Component {
  //values, put in state so they can be updated/stored
  state = {
    total: "",
    service: "",
    partySize: "",
    totalTip: "",
    tipPP: "",
  };

  handleTotal = (event) => {
    total: this.setState = this.target.value;
  };
  //controlled form - we set values equal to something in state
  render() {
    return (
      <div>
        <h1>Calculator</h1>
        <form>
          <label>
            What is your total?
            <input
              type="text"
              value={this.state.total}
              onChange={this.handleTotal}
            ></input>
          </label>
          <label>
            How was your service?
            <input
              type="text"
              value={this.state.service}
              onChange={this.handleService}
            ></input>
          </label>
          <label>
            What is your party size?
            <input
              type="text"
              value={this.state.partySize}
              onChange={this.handleParty}
            ></input>
          </label>
          <input type="submit"></input>
        </form>
      </div>
    );
  }
}

export default TipCalc;
