import React, { useState } from "react";

function PlacesToGo() {
  const [todos, setTodos] = useState(["adjfhaksjhdflkjashd", "POOP SOUP"]);
  //preparing state to keep track of user input in user field
  const [input, setInput] = useState("");
  const addTodo = (e) => {
    e.preventDefault();
    setTodos([...todos, input]);
    setInput("");
  };

  return (
    <div>
      <h2>Which place shall we go?</h2>
      <form>
        <input
          value={input}
          onChange={(e) => setInput(e.target.value)}
          type="text"
        ></input>
        <button type="submit" onClick={addTodo}>
          Add Place to GO
        </button>
      </form>

      <h3>List of To-DOSSSSS</h3>
      {todos.map((todo) => (
        <p>{todo}</p>
      ))}
    </div>
  );
}

export default PlacesToGo;
